class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> t(n + 1);
        vector<int> fushu(n + 1);
        for (int i = 0; i < n; i++) {
            fushu[i+1] = t[i+1] = nums[i] - target[i];
            if (fushu[i+1] > 0) fushu[i+1] = 0;
            if (t[i+1] < 0) t[i+1] = 0;
        }
        for (int i = 1; i <= n; i++) {
            int u = fushu[i - 1] - fushu[i];
            if (u > 0) ans += u;
        }
        for (int i = 1; i <= n; i++) {
            int u = t[i] - t[i - 1];
            if (u > 0) ans += u;
        }
        return ans;
    }
};
