class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        long long n = a.size(), ans = 0;
        for(int i = 0; i < n; i++){
            if(i == 0) ans += abs(a[i] - b[i]);
            else{
                int L = a[i - 1] - b[i - 1];
                int R = a[i] - b[i];
                if(L < 0){
                    if(R < 0){
                        if(R < L) ans += abs(R - L);
                    }
                    else ans += R;
                }
                else{
                    if(R > 0){
                        if(R > L) ans += R - L;
                    }
                    else ans += abs(R);
                }
            }
        }
        return ans;
    }
};
