class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        int si = nums.size();
        for(int i=0;i<si;i++){
            diff.push_back(target[i]-nums[i]);
        }   
        long long ans = 0;
        int inc = 0;
        int dec = 0;
        for(int i=0;i<si;i++){
            if(diff[i] == 0){
                inc = 0;
                dec = 0;
            }
            else if(diff[i]>0){
                if(inc > diff[i]){
                    inc = diff[i];
                }
                else if(inc < diff[i]){
                    ans += diff[i]-inc;
                    inc = diff[i];
                }
                dec = 0;
            }
            else{
                if(dec <diff[i]){
                    dec = diff[i];
                }
                else if(dec > diff[i]){
                    ans += dec-diff[i];
                    dec = diff[i];
                }
                inc = 0;
            }
        }
        return ans;
    }
};
