class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int index = 0;

        for(auto &x : nums)
        {
            x = target[index++] - x;
        }

        ans = abs(nums[0]);
        for(int i = 1; i < nums.size(); i++)
        {
            long long m = 1LL * nums[i] * nums[i-1];
            if(m <= 0)
            {
                ans += abs(nums[i]);
            }
            else 
            {
                ans += max(0, abs(nums[i]) - abs(nums[i-1]));
            }
        }
        return ans;
    }
};
