class Solution {
public:
    #define ll long long

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size();
        vector<ll> dif(n, 0);
        for(int i = 0; i < n; i++)
        {
            dif[i] = nums[i] - target[i];
        }
        ll ans = 0;
        for(int i = 0; i < n; i++)
        {
            if(dif[i] > 0)
            {
                ans += dif[i];
                i++;
                while(i < n && dif[i] > 0 && dif[i] <= dif[i-1])
                {
                    i++;   
                }
                ll j = i, m = dif[j-1];
                while(j < n && dif[j] > 0)
                {
                    m = min(m, dif[j]);
                    dif[j] -= m;
                    j++;
                }
                i--;
            }
            else if(dif[i] < 0)
            {
                ans += abs(dif[i]);
                i++;
                while(i < n && dif[i] < 0 && abs(dif[i]) <= abs(dif[i-1]))
                {
                    i++;
                }       
                ll j = i, m = dif[j-1];
                while(j < n && dif[j] < 0)
                {
                    m = max(m, dif[j]);
                    dif[j] -= m;
                    j++;
                }
                i--;
            }
        }
        return ans;
    }
};
