class Solution {
public:
    long long minimumOperations(vector<int> &nums, vector<int> &target) {
        vector<int> diff;
        diff.reserve(target.size());
        for (int i = 0; i < target.size(); ++i) {
            diff.push_back(nums[i] - target[i]);
        }
        long long ans = std::abs(diff[0]);
        for (int i = 1; i < diff.size(); ++i) {
            int a = diff[i - 1], b = diff[i];
            if (a < 0 && b < 0) {
                ans += max(abs(b) - abs(a), 0);
            } else if (a > 0 && b > 0) {
                ans += max(abs(b) - abs(a), 0);
            } else {
                ans += abs(b);
            }
        }
        return ans;
    }
};
