class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [target[i] - nums[i] for i in range(len(nums))]

        ans = 0
        prev = 0

        for diff in diffs:
            if prev * diff < 0:
                prev = 0
            ans += max(abs(diff) - abs(prev), 0)
            prev = diff

        return ans
