class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& t) {
        int n=a.size();
        int pd=0,ld=0;
        long long ans=0;
        for(int i=0;i<n;i++){
            int d= (t[i]-a[i]);
            if(d<0){
                if(pd>0){
                    ld=0;
                }
                if(d<ld){
                    ans += (ld-d);
                    ld=d;
                }
                else{
                    ld=d;
                }
            }
            else{
                if(pd<0){
                    ld=0;
                }
                if(d>ld){
                    ans+= (d-ld) ;
                    ld=d;
                }
                else{
                    ld=d;
                }
            }

            pd=d;
        }
        return ans;
    }
};
