impl Solution {
    pub fn minimum_operations(nums: Vec<i32>, target: Vec<i32>) -> i64 {
        let n = nums.len();
        let mut ds: Vec<_> = (0..n).map(|i| (target[i] - nums[i]) as i64).collect();
        let mut ans = 0;
        let mut t = 0;
        for i in 0..n {
            if ds[i] == 0 {
                t = 0;
                continue;
            }
            if (ds[i] < 0) ^ (t < 0) {
                ans += ds[i].abs();
                t = ds[i];
                continue;
            }
            ans += (ds[i].abs() - t.abs()).max(0);
            t = ds[i];
        }

        ans
    }

}
