class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        long long ans = 0;
        for (int i = 0; i < n; i++) {
            nums[i] -= target[i];

            if (i == 0)
                ans += abs(nums[i]);
            else {
                if ((nums[i - 1] < 0) != (nums[i] < 0))
                    ans += abs(nums[i]);
                else {
                    if (nums[i] < 0)
                        ans += max(0, nums[i - 1] - nums[i]);
                    else
                        ans += max(0, nums[i] - nums[i - 1]);
                }
            }
            cout << ans << ' ' << nums[i] << '\n';
        }
        return ans;
    }
};
