class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        ans = abs(target[0] - nums[0]);
        for(int i = 1;i < nums.size();++i)
        {
            if(nums[i - 1]  >= target[i - 1])
            {
                int cha1 = nums[i - 1] - target[i - 1];
                if(nums[i] >= target[i])
                {
                    int cha2 = nums[i] - target[i];
                    if(cha2 <= cha1)continue;
                    else ans += cha2 - cha1;
                }else
                {
                    ans +=  target[i] - nums[i];
                }
            }else
            {
                int cha1 = target[i - 1] - nums[i - 1];
                if(nums[i] <= target[i])
                {
                    int cha2 = target[i] - nums[i];
                    if(cha2 <= cha1)continue;
                    else ans += cha2 - cha1;
                }else
                {
                    ans += nums[i] - target[i] ;
                }
            }
        }
        return ans;

    }
};
