class Solution {
public:
    long long minimumOperations(vector<int>& ar, vector<int>& t) {
        long long ans=0,c=0,dif=0;
        for(int i=0;i<ar.size();i++)
        {
            dif=t[i]-ar[i];
            if(c<0&&dif<0)
            {
                if(dif<c)
                {
                    ans+=abs(dif-c);
                    c=dif;
                }
                else
                {
                    c=dif;
                }
            }
            else if(c>0&&dif>0)
            {
               if(dif>c)
               {
                   ans+=abs(dif-c);
                   c=dif;
               }
                else
                {
                    c=dif;
                }
            }
            else
            {
                c=dif;
                ans+=abs(dif);
            }
        }
        return ans;
    }
};
