class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>req(n,0);

        for(int i=0;i<n;i++){
            req[i] = target[i]-nums[i];
        }
        int prev =0;
        long long ans = 0;
        for(auto &x:req){
            if(x>=0 && prev>=0){
               if(x>prev){
                 ans+=(x-prev);
               }

            }
            else if(x<=0 && prev<=0){
                if(prev>x){
                    ans+=(prev-x);
                }
            }
            else if(x>=0 && prev<=0){
                ans+=x;
            }
            else if(x<=0 && prev>=0){
                ans+=abs(x);
            }


            prev = x;
        }
        return ans;
    }
};
