class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        const int n = a.size();
        vll d(n);

        rep(i, 0, n) {
            d[i] = b[i] - a[i];
        }

        ll res = 0;
        ll cur = 0;

        rep(i, 0, n) {
            if (d[i] == 0) {
                cur = 0;
                continue;
            }

            if (d[i] > 0) {
                if (cur < 0) {
                    cur = 0;
                }
                if (cur < d[i]) {
                    res += d[i] - cur;
                }
                cur = d[i];
            } else {
                if (cur > 0) {
                    cur = 0;
                }
                if (cur > d[i]) {
                    res += cur - d[i];
                }
                cur = d[i];
            }
        }

        return res;
    }
};
