class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        long long inc = 0, dec = 0;
        long long n = nums.size();
        for(int i=0;i<n;i++){
            if(nums[i] < target[i]){
                if(inc != 0){
                    int temp = target[i] - nums[i];
                    if(temp > inc){
                        res += temp - inc;
                    }
                    inc = temp;                  
                }
                else{
                    inc = target[i] - nums[i];
                    res += inc;
                }
                dec = 0;
            }
            else{
                if(dec != 0){
                    int temp = nums[i] - target[i];
                    if(temp > dec){
                        res += temp - dec;
                    }
                    dec = temp;                  
                }
                else{
                    dec = nums[i] - target[i];
                    res += dec;
                }
                inc = 0;
            }
        }

        return res;
    }
};
