class Solution {
    public long minimumOperations(int[] nums, int[] target) {

        int n = nums.length;
        long res = 0;

        for (int i = 0; i < n; i++) {
            nums[i] = target[i] - nums[i];
        }

        int cur = 0;

        for (int i = 0; i < n; i++) {

            boolean p = nums[i] >= 0;

            if (cur >= 0 != p) {
                cur = nums[i];
                res += Math.abs(cur);
                continue;
            }

            if (p) {
                if (cur == nums[i]) {
                    continue;
                }

                if (cur < nums[i]) {
                    res += nums[i] - cur;
                    cur = nums[i];
                } else {
                    cur = nums[i];
                }
            } else {

                if (cur == nums[i]) {
                    continue;
                }

                if (cur < nums[i]) {
                    cur = nums[i];
                } else {
                    res += cur - nums[i];
                    cur = nums[i];
                }
            }


        }



        return res;
    }
}
