class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long inc = 0, dec = 0;
        int n = nums.size();
        long long ans = 0;
        for(int i = 0; i < n; i++){
            if(nums[i] < target[i]){
                long long dif = target[i] - nums[i];
                if(inc < dif){
                    ans += dif - inc;
                }
                inc = dif;
                dec = 0;
            }
            else if(nums[i] > target[i]){
                long long dif = nums[i] - target[i];
                if(dec < dif){
                    ans += dif - dec;
                }
                dec = dif;
                inc = 0;
            }
            else{
                inc = 0;
                dec = 0;
            }
        }

        return ans;
    }
};
