class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        mountains = []
        nums = [n - t for n, t in zip(nums, target)]
        i, cur = 0, abs(nums[0])
        res = 0
        while i + 1 < len(nums):
            n1, n2 = nums[i], nums[i + 1]
            if n1 * n2 <= 0:
                res += cur
                cur = abs(n2)
            else:
                if abs(n2) > abs(n1):
                    cur += abs(n2) - abs(n1)
            i += 1
        res += cur
        return res
