class Solution {
	public long minimumOperations(int[] nums, int[] target) {
		int n = nums.length;
		int[] diff = new int[n];
		for (int i = 0; i < n; i++) {
			diff[i] = target[i] - nums[i];
		}
		long cnt = Math.abs(diff[0]);
		for (int i = 1; i < n; i++) {
			if (diff[i] > 0 && diff[i - 1] < 0 || diff[i - 1] > 0 && diff[i] < 0) {
				cnt += Math.abs(diff[i]);
			} else {
				cnt += Math.abs(diff[i]) > Math.abs(diff[i - 1]) ? Math.abs(diff[i - 1] - diff[i]) : 0;
			}
		}
		return cnt;
	}
}
