class Solution {
public:
    long long getAns(vector<long long> &v) {
        int n = v.size();
        long long ans = v[0];
        for (int i = 1; i < n; i++) {
            if (v[i] > v[i - 1]) ans += (v[i] - v[i - 1]);
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        int n = nums.size();
        vector<int> a;
        for (int i = 0; i < n; i++) a.push_back(tar[i]- nums[i]);

        int prev = (a[0] < 0) ? -1 : 1;
        vector<vector<long long>> v;
        vector<long long> b;
        if (prev < 0) {
            b.push_back(a[0] * (-1));
        }
        else {
            b.push_back(a[0]);
        }
        for (int i = 1; i < n; i++) {
            if (prev > 0) {
                if (a[i] < 0) {
                    v.push_back(b);
                    b.clear();
                    prev = -1;
                    b.push_back(a[i] * (-1));
                }
                else {
                    b.push_back(a[i]);
                }
            }
            else {
                if (a[i] >= 0) {
                    v.push_back(b);
                    b.clear();
                    prev = 1;
                    b.push_back(a[i]);
                }
                else {
                    b.push_back(a[i] * (-1));
                }
            }
        }
        v.push_back(b);

        long long ans = 0;
        for (auto u: v) {
            ans += getAns(u);
        }

        return ans;
    }
};
