class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> a(n);
        for (int i=0;i<n;i++) {
            a[i] = target[i] - nums[i];
        }
        int currSign = a[0] > 0;

        long long  ans = 0;
        stack<int> st;
        for (int i=0;i<n;i++) {
            if (st.empty() && a[i] != 0) {
                st.push(a[i]);
            }
            else if (!st.empty()) {
                if (st.top() > 0 && a[i] > st.top()) ans += a[i] - st.top();
                else if (st.top() < 0 && a[i] < st.top()) ans += st.top() - a[i];
                while (!st.empty() && st.top() >= 0 && a[i] <= 0) {
                    if (st.size() == 1) ans += st.top();
                    st.pop();
                }
                while (!st.empty() && st.top() <= 0 && a[i] >= 0) {
                    if (st.size() == 1) ans += abs(st.top());
                    st.pop();
                }
                while (!st.empty() && a[i] > 0 && st.top() >= a[i]) {
                    if (st.size() == 1) ans += abs(a[i] - st.top());
                    st.pop();
                }
                while (!st.empty() && a[i] < 0 && st.top() <= a[i]) {
                    if (st.size() == 1) ans += abs(a[i] - st.top());
                    st.pop();
                }
                if (a[i] == 0) continue;
                st.push(a[i]);
            }
        }

        while (!st.empty()) {
            if (st.size() == 1) ans += abs(st.top());
            st.pop();
        }


        return ans;
    }
};
