class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        A = [a - b for a, b in zip(nums, target)]
        dif = [A[0]]
        for i in range(1, len(A)):
            dif.append(A[i] - A[i - 1])
        dif.append(-A[-1])
        res = more = less = 0
        for d in dif:
            if d > 0:
                if d > less:
                    res += d - less
                    more += d - less
                    less = 0
                else:
                    less -= d
            elif d < 0:
                d = -d
                if d > more:
                    res += d - more
                    less += d - more
                    more = 0
                else:
                    more -= d
        return res
