class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def sign(x: int) -> int:
            if x > 0:
                return 1
            if x < 0:
                return -1
            return 0

        differences = [t - n for n, t in zip(nums, target)]
        answer = abs(differences[0])
        for i in range(1, len(differences)):
            if sign(differences[i-1]) != sign(differences[i]):
                answer += abs(differences[i])
                continue
            answer += max(0, abs(differences[i]) - abs(differences[i-1]))
        return answer
