class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> c(n + 1);
        for(int i = 1; i <= n; i ++) {
            c[i] = target[i - 1] - nums[i - 1];
        }

        long long pos = 0, neg = 0;
        for(int i = 1; i <= n; i ++) {
            if(c[i] - c[i - 1] > 0) pos += c[i] - c[i - 1];
            else neg += c[i - 1] - c[i];
        }

        return max(pos, neg);
    }
};
