class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        n = len(nums)
        diff = [0]*n
        for i in range(n):
            diff[i] =  target[i]-nums[i]



        def help(l, r) -> int:
            res = abs(diff[l])
            for i in range(l+1, r+1):
                if abs(diff[i]) >= abs(diff[i - 1]):
                    res -= abs(diff[i - 1])
                    res += abs(diff[i])

            return res

        #print(diff)
        sn, l = diff[0], 0
        ans = 0
        for j in range(n):
            if sn*diff[j] <= 0:
                ans += help(l, j-1)
                l = j
                sn = diff[j]
        ans += help(l, n-1)
        return ans
