#define ll long long int
class Solution {
ll minNumberOperations2(vector<int>& target) {
        vector<int> stack;
        ll res = 0;

        stack.push_back(target[0]);
        for (int i = 1; i < target.size(); i++) {
            if (target[i] > stack.back()) {
                stack.push_back(target[i]);
                continue;
            } 
            if (target[i] == stack.back())
                continue;

            int top = stack.back();

            while (!stack.empty() && target[i] < stack.back())
                stack.pop_back();

            res += top - target[i];
            stack.push_back(target[i]);
        }

        if (!stack.empty())
            res += stack.back();

        return res;
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int>temp1(n,0),temp2(n,0);
        for(int i=0;i<n;i++){
            if(nums[i]-target[i]>0) temp1[i]=nums[i]-target[i];
            else temp2[i]=-(nums[i]-target[i]);
        }
        return minNumberOperations2(temp1)+minNumberOperations2(temp2);
    }
};
