class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        result = 0
        prev = 0
        for i in range(len(nums)):
            diff = target[i] - nums[i]
            if diff * prev <= 0:
                result += abs(diff)
            elif abs(diff) > abs(prev):
                result += abs(diff) - abs(prev)
            prev = diff
        return result
