class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int[] temp = new int[nums.length];
        for (int i = 0; i < nums.length; i++) {
            temp[i] = target[i] - nums[i];
        }
        long res = 0L;
        int t = 0;
        for (int i = 0; i < temp.length; i++) {
            int cur = temp[i];
            if (t >= 0 && cur >= 0) {
                if (t >= cur) {
                    t = cur;
                    continue;
                }
                else {
                    res += cur - t;
                    t = cur;
                }
            }
            else if (t < 0 && cur < 0) {
                if (t <= cur) {
                    t =cur;
                    continue;
                }
                else {
                    res += t - cur;
                    t = cur;
                }
            }
            else {
                t = cur;
                res += Math.abs(cur);
            }
        }
        return res;
    }
}
