class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>dif(nums.size(),0);
        for(int i = 0; i<nums.size(); i++){
            dif[i] = target[i]-nums[i];
        }
        int prev = 0;
        long long ans = 0;
        for(int i = 0; i<nums.size(); i++){

            if(dif[i]<=0&&prev>=0){
                prev = dif[i];
                ans+=abs(dif[i]);
                continue;
            }else if(dif[i]>=0&&prev<=0){
                prev = dif[i];
                ans+=dif[i];
                continue;
            }else if(dif[i]>=0&&prev>=0){
                if(dif[i]>prev){
                    ans+=dif[i]-prev;
                }
                prev = dif[i];
            }else if(dif[i]<=0&&prev<=0){
                if(dif[i]<prev){
                    ans+=prev-dif[i];
                }
                prev = dif[i];
            }

        }
        return ans;
    }
};
