long long minimumOperations(int* nums, int numsSize, int* target, int targetSize) {
    int dif[numsSize];
    for (int i = 0; i < numsSize; ++i) {
        dif[i] = target[i] - nums[i];
    }
    int pre = dif[0];
    long long ret = abs(pre);
    int x, y;
    for (int i = 1; i < numsSize; ++i) {
        if (dif[i] == 0) {
            pre = 0;
            continue;
        }
        x = dif[i] > 0 ? 1 : -1;
        y = pre > 0 ? 1 : pre == 0 ? 0 : -1;
        if (x * y > 0) {
            if (abs(dif[i]) > abs(pre))
                ret += abs(dif[i]) - abs(pre);
            pre = dif[i];
        } else {
            pre = dif[i];
            ret += abs(dif[i]);
        }
    }
    return ret;
}
