class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long pos = 0, neg = 0;
        long [] diff = new long[n];
        for(int i = 0; i<n; i++){
            diff[i] = target[i]-nums[i];
        }
        long ans = 0;
        for(int i = 0; i<n; i++){
            if(diff[i]>=0){
                ans += Math.max(diff[i]-pos, 0);
                pos = diff[i];
                neg = 0;
            }
            else{
                ans += Math.max(Math.abs(diff[i])-neg, 0);
                neg = Math.abs(diff[i]);
                pos = 0;
            }
        }
        return ans;
    }
}
