class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int islarge = 3; // 大为真
        int opnum = 0;
        int n = nums.size();
        long long ans = 0;
        for(int i = 0 ; i < n ; i++){
            int op = nums[i] - target[i];
            if(op == 0){ 
                opnum = 0;
                islarge = 0;
                continue;
            }
            if(op > 0 && islarge > 0){
                if(opnum < op) ans += op - opnum;
            }
            else if(op < 0 && islarge < 0){
                op = abs(op);
                if(opnum < op) ans += op - opnum;
            }
            else{
                ans += abs(op);
                islarge = op;
            }
            opnum = abs(op);
            //cout << opnum << endl;
        }
        return ans;
    }
};
