class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (size_t i = 0; i < nums.size(); ++i) {
            target[i] -= nums[i];
        }
        long long ans = 0;
        int pre = 0;
        for (int i : target) {
            if (i > 0) {
                if (pre > 0) {
                    ans += max(i - pre, 0);
                } else {
                    ans += i;
                }
            }
            if (i < 0) {
                if (pre < 0) {
                    ans += max(pre - i, 0);
                } else {
                    ans -= i;
                }
            }
            pre = i;
        }
        return ans;
    }
};
