class Solution:
    def minimumOperations(self, nums: List[int], tar: List[int]) -> int:
        n = len(nums)
        d = [tar[i]-nums[i] for i in range(n)]
        ans = 0
        mx = 0
        for i in range(n):
            if d[i]>0:
                if i>0 and d[i-1]>0 and d[i]>d[i-1]:
                    ans += d[i]- d[i-1]
                    mx = d[i]
                elif mx<d[i]:
                    ans += d[i]-mx
                    mx = d[i]
            else:
                mx = 0
        mx = 0
        #print(ans)
        for i in range(n):
            if d[i]<0:
                if i>0 and d[i-1]<0 and d[i]<d[i-1]:
                    ans += abs(d[i]- d[i-1])
                    mx = d[i]
                elif mx>d[i]:
                    ans += abs(d[i]-mx)
                    mx = d[i]
            else:
                mx = 0
        return ans
