class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        stack<int> st;
        int n = nums.size();
        vector<int> cf(n);
        for (int i = 0; i < n; ++i)
            cf[i] = target[i] - nums[i];
        st.push(cf[0]);
        long long ans = abs(cf[0]);
        for (int i = 1; i < n; ++i) {
            int x = st.top();
            int sx, si;
            sx = x > 0 ? 1 : -1;
            si = cf[i] > 0 ? 1 : -1;
            if (x == 0)
                sx == 0;
            if (cf[i] == 0)
                si == 0;
            if (sx*si <= 0) {
                while (!st.empty())
                    st.pop();
                st.push(cf[i]);
                ans += abs(cf[i]);
            } else {
                if (abs(cf[i]) <= abs(x)) {
                    st.push(cf[i]);
                    continue;
                } else {
                    ans += abs(cf[i]) - abs(x);
                    st.push(cf[i]);
                }
            }
        }
        return ans;
    }
};
