class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> dn(n);
        vector<int> dt(n);
        for(int i=0;i<n;++i){
            if(i==0){
                dn[i]=nums[i];
                dt[i]=target[i];
                continue;
            }
            dn[i]=nums[i]-nums[i-1];
            dt[i]=target[i]-target[i-1];
        }
        long long pos=0;
        long long neg=0;
        for(int i=0;i<n;++i){
            if(dt[i]>dn[i]){
                pos+=dt[i]-dn[i];
            }else{
                neg+=dn[i]-dt[i];
            }
        }
        return max(pos,neg);
    }
};
