class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> num(n+1),tar(n+1);
        num[0] = 0;
        tar[0] = 0;
        num[1] = nums[0];
        tar[1] = target[0];
        for(int i=1; i<n; i++){
            num[i+1] = nums[i] - nums[i-1];
            tar[i+1] = target[i] - target[i-1];
        }
        long long presum = 0;
        long long ans = 0;
        for(int i=1; i<=n; i++){
            if(num[i]!=tar[i]){
                int diff = num[i] - tar[i];
                if(diff * presum >= 0){
                    ans += abs(diff);
                    presum += diff;
                }else{
                    presum += diff;
                    if(diff * presum >0){
                        ans += abs(presum);
                    }
                }
            }
        }
        return ans;
    }
};
