class Solution {
public:
    int cal(vector<int>& A) {
            int res = 0, pre = 0;
            for (int a: A) {
                res += max(a - pre, 0);
                pre = a;
            }
            return res;
        }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>arr;
        for(int i=0;i<nums.size();i++){
            arr.push_back(target[i]-nums[i]);
        }
        vector<vector<int>> result;
        vector<int> current;
        for (size_t i = 0; i < arr.size(); ++i) {
            int value = arr[i];
            if (current.empty() || (current.back() > 0 && value > 0) || (current.back() < 0 && value < 0)) {
                current.push_back(value);
            } else {
                if (current[0] < 0) {
                    for (auto& num : current) {
                        num = abs(num);
                    }
                }
                result.push_back(current);
                current.clear();
                current.push_back(value);
            }
        }
        if (!current.empty()) {
            if (current[0] < 0) {
                for (auto& num : current) {
                    num = abs(num);
                }
            }
            result.push_back(current);
        }
        long long res=0;
        for (auto subArray : result) {
            res+=cal(subArray);
        }
        return res;
    }
};
