class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;i++) {
            diff[i] = target[i] - nums[i];
        }
        long long ans=0, cur=0;
        for(int i=0;i<n;i++) {
           // cout<<diff[i]<<" "<<cur<<"\n";
            if(1ll*diff[i] == cur) continue;
            if(abs(cur) < abs(1ll*diff[i]) && cur*diff[i] >=0) {
                cur = diff[i];
            }
            else {
                int cnt;
                if(cur*diff[i] >=0 )
                cnt = abs(cur - diff[i]);
                else {
                    cnt  = abs(cur);
                }
                ans+=cnt;
                cur = diff[i];
            }
        }
        ans+= abs(cur);
        return ans;

    }
};
