class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        long long ans = 0;

        vector<int> v;

        // v.push_back(0);

        for(int i=0;i<nums.size();i++){
            v.push_back(nums[i]- target[i]);
        }

        int i=0;
        int n = nums.size();

        while(i< n){
            vector<int> a;
            int j = i;
            if(v[i]< 0){
                a.push_back(0);
                while(j< n && v[j]<= 0){
                    a.push_back(v[j]);
                    j++;
                }

                for(int k = 1;k< a.size();k++){
                    if(a[k]< a[k-1]){
                        ans += a[k-1]- a[k];
                    }
                }
            }
            else{
                a.push_back(0);
                while(j< n && v[j]>= 0){
                    a.push_back(v[j]);
                    j++;
                }

                for(int k = 1;k < a.size(); k++){
                    if(a[k]> a[k-1]){
                        ans += a[k]- a[k-1];
                    }
                }


            }
            i = j;
        }

        return ans;
    }
};
