class Solution {
     long long int minNumberOperations(vector<int>& target) {
      long long int ans=0,d=0,k=0;
        int n=target.size();
        for(int i=0;i<n;i++){
            d=target[i]-k;
            while(i<n-1 && target[i]<=target[i+1]){
                i++;
                d=max(target[i]-k,d);
            }
            while(i<n-1 && target[i]>=target[i+1]){
                i++;
                d=max(d,target[i]-k);
            }
            ans+=d;
            if(i<n){
                k=target[i];
            }
        }
        return ans;
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int ans=0;
        // vector<int>l;
       vector<vector<int>>o;
        int n=nums.size();
        map<int,int>m;
        for(int i=0;i<nums.size();i++){
            vector<int>l;
            while(i<n && nums[i]<target[i]){
                l.push_back(target[i]-nums[i]);
                i++;
            }
            if(l.size()>0){
                o.push_back(l);
            }
            while(i<n && nums[i]==target[i]){
                // l.push_back(target[i]-nums[i]);
                i++;
            }
            vector<int>p;
             while(i<n && nums[i]>target[i]){
                p.push_back(abs(target[i]-nums[i]));
                i++;
            }
            if(p.size()>0){
                o.push_back(p);
            }
            if(i!=n && m[i]==0){
                i--;
                m[i]++;
            }

        }
        for(auto &pr:o){
            ans+=minNumberOperations(pr);
        }
        return ans;
    }
};
