class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        vector<int> a(nums.size() + 1), d(a.size());
        for(int i = 1; i <= nums.size(); i++){
            a[i] = t[i - 1] - nums[i - 1];
        }

        for(int i = 1; i < a.size(); i++){
            d[i - 1] = a[i] - a[i - 1];
        }

        long long qwq = 0, owo = 0;
        for(int i = 0; i < d.size(); i++){
            if(d[i] > 0) qwq += d[i];
            else owo -= d[i] ;
        }

        return owo > qwq ? owo : qwq;
    }
};
