#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tg) {

        ll ans = 0;
        int prev = 0;
        int n = nums.size();
        for(int i = 0; i < n; i++)
        {
            int diff = tg[i] - nums[i];

            if(diff >= 0)
            {
                if(prev >= 0)
                {
                    if(prev >= diff){
                        prev = diff;
                        continue;
                    } 
                    else
                        ans += diff - prev;
                }
                else
                {
                    ans += diff;
                }
            }
            else
            {
                if(prev < 0)
                {
                    if(prev <= diff){
                        prev = diff;
                    continue;
                    } 
                    else
                        ans += prev - diff;
                }
                else
                    ans += abs(diff);
            }
            // cout << i << " " << ans << endl;
            prev = diff;
        }
        return ans;
    }
};
