class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ops=0;
        int n=nums.size();
        nums.push_back(0);
        target.push_back(0);
        nums.insert(nums.begin(), 0);
        target.insert(target.begin(), 0);
        for(int i=0; i<=n; i++){
            ops+=abs(nums[i]-nums[i+1]-(target[i]-target[i+1]));
        }
        return ops/2;
    }
};
