class Solution {
    public long minimumOperations(int[] nums, int[] target) {

        int n = nums.length;

        long result = 0;
        int preDiff = 0;

        for (int i = 0; i < n; i++) {
            int diff = target[i] - nums[i];
            if (preDiff >= 0 && diff < 0 || preDiff <= 0 && diff > 0) {
                    preDiff = 0;
                }
            if (preDiff >= 0 && diff > 0) {
                if (preDiff < diff) {
                    result += diff - preDiff;
                }
            }

            if (preDiff <= 0 && diff < 0) {
                if (preDiff > diff) {
                    result += preDiff - diff;
                }
            }

            preDiff = diff;
        }

        return result;

    }
}
