class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> v(n);
        for (int i = 0; i < n; ++i) v[i] = nums[i] - target[i];
        for (auto x: v) cout << x << ' ';
        long long curr = 0LL, ans = 0LL;
        for (int i = 0; i < n; ++i) {
            if (curr == 0) {
                curr = -v[i];
                ans += abs(v[i]);
            } else if (v[i] == 0) {
                curr = 0;
            }
            else {
                if (1LL * curr * v[i] < 0LL) {
                    if (abs(curr) < abs(v[i])) {
                        ans += abs(v[i]) - abs(curr);
                        curr = -v[i];
                    } else {
                        curr = -v[i];
                    }
                } else {
                    curr = -v[i];
                    ans += abs(v[i]);
                }
            }
        }
        return ans;
    }
};
