class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
         int n = nums.size();

        int i = 0;
        long long ans = 0;
        vector<int> v(n,0);

        for(int i=0;i<n;i++){
            v[i] = nums[i]-target[i];
        }

        while(i<n){
            if(v[i]==0){
                i++;
                continue;
            }
            if(v[i]>0){
                int mini = v[i];
                int j = i+1;

                while(j<n && v[j]>0){
                    mini = min(mini,v[j]);
                    j++;
                }

                for(int k=i;k<j;k++){
                    v[k]-=mini;
                }
                ans+=mini;
            }else{
                int maxi = v[i];
                int j = i+1;
                while(j<n && v[j]<0){
                    maxi = max(maxi,v[j]);
                    j++;
                }

                for(int k=i;k<j;k++){
                    v[k]-=maxi;
                }
                ans+=abs(maxi);
            }
        }
        return ans;
    }
};
