class Solution {
public:

    long long solve(vector<int> &nums){
        long long ans=0;
        vector<vector<int>> segments;
        if (nums.size() <= 1) return nums[0];

        vector<int> currentSegment = {nums[0]};
        bool isIncreasing = nums[1] > nums[0];

        for (size_t i = 1; i < nums.size(); ++i) {
            if ((nums[i] > nums[i - 1]) == isIncreasing) {
                currentSegment.push_back(nums[i]);
            } else {
                segments.push_back(currentSegment);
                currentSegment = {nums[i]};
                isIncreasing = !isIncreasing;
            }
        }
        segments.push_back(currentSegment);

        long long end=0;
        for(auto i: segments){
            ans+=max(0LL,max(i[0],i[i.size()-1])-end);
            end=i[i.size()-1];
        }
        return ans;
    }

    long long minimumOperations(vector<int>& v, vector<int>& tt) {
        long long ans=0;
        int n=v.size();
        for(int i=0;i<n;i++){
            v[i]=v[i]-tt[i];
        }

        int ss = v[0] > 0 ? 1 : (v[0] < 0 ? -1 : 0);
        vector<vector<int>> val;
        vector<int> cur;
        for(int i=0;i<n;i++){
            int num=v[i];
            int ts=num > 0 ? 1 : (num < 0 ? -1 : 0);
            if(ss==ts){
                cur.push_back(num);
            }else{
                val.push_back(cur);
                cur={num};
                ss=ts;
            }
        }val.push_back(cur);


        for(auto i: val){
            if(i[0]<0){
                for(auto &j: i) j=-j;
            }
            ans+=solve(i);
        }
        return ans;
    }
};
