class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int ans=0;
        long long int ma=0;
        long long int l=0;
        for(long long int i=0;i<nums.size();i++){
             if(target[i]>nums[i]){
                if(l==1){
                   if(ma>target[i]-nums[i]){
                      ma=target[i]-nums[i];
                   }
                   else{
                      ans+=(target[i]-nums[i]-ma);
                      ma=target[i]-nums[i];
                   }
                }
                else{
                   ans+=target[i]-nums[i];
                   ma=target[i]-nums[i];
                   l=1;
                }
             }
             else if(target[i]<nums[i]){
                if(l==-1){
                   if(ma<target[i]-nums[i]){
                      ma=target[i]-nums[i];
                   }
                   else{
                      ans+=(ma-(target[i]-nums[i]));
                      ma=target[i]-nums[i];
                   }
                }
                else{
                   ans+=nums[i]-target[i];
                   ma=target[i]-nums[i];
                   l=-1;
                }
             }
             else{
                l=0;
                ma=0;
             }
        }
        return ans;
    }
};
