class Solution {
public:
    #define ll long long
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n=nums.size(),ans=0;
        vector<ll> v(n);
        for(int i=0;i<n;i++){
            v[i] = target[i]-nums[i];
            // cout<<v[i]<<" ";
        }
        // cout<<endl;

        for(int i=0;i<n;){
            ll val=v[i],j=i;
            while(j<n && ((v[j]<0 && val<0) || (v[j]>=0 && val>=0))){
                j++;
            }

            // ll maxi=INT_MIN,maxiInd=i,mini=INT_MAX,miniInd=i;
            // cout<<i<<" "<<j<<endl;
            ans += abs(v[i]);
            for(int p=i;p<j;p++){
                ll it = abs(v[p]);
                // ans += it;
                // if(maxi<it){maxi=it;maxiInd=p;}
                // if(mini>it){mini=it;miniInd=p;}

                if(p-1>=i){
                    if(abs(v[p])>abs(v[p-1])) ans+=abs(v[p])-abs(v[p-1]);
                    // else if(abs(v[p])<abs(v[p-1]))
                }
            }

            i=j;
        }

        return ans;
    }
};
